import React, {useEffect, Component } from 'react';
import ReactDOM from "react-dom";
import socketIOClient from "socket.io-client";
import 'react-chat-widget/lib/styles.css';
import ChatRoomHeader from './chatRoomHeader';
import { makeStyles } from '@material-ui/styles'
import { GiftedChat } from 'react-web-gifted-chat';


const ENDPOINT = "http://localhost:3000/"
const APILINK = 'https://matchsitebackend.herokuapp.com/msg/get';


function mysqlTimeStampToDate(timestamp) {
    //function parses mysql datetime string and returns javascript Date object
    //input has to be in this format: 2007-06-05 15:26:02
    var regex=/^([0-9]{2,4})-([0-1][0-9])-([0-3][0-9])T(?:([0-2][0-9]):([0-5][0-9]):([0-5][0-9])).000Z?$/;
    var parts=timestamp.replace(regex,"$1 $2 $3 $4 $5 $6").split(' ');
    return new Date(parts[0],parts[1]-1,parts[2],parts[3],parts[4],parts[5]);
}

const useStyles = makeStyles((theme) => ({
    innerDiv: {
      zIndex: '1',  
      boxSizing: 'border-box',
      backgroundColor: 'white',
      backgroundSize: 'auto 100%',
      backgroundRepeat: 'no-repeat',
      backgroundPosition: 'center center',
      boxShadow: '0 12.5px 100px -10px rgba(90, 90, 90, 0.4), 0 10px 10px -10px rgba(90, 90, 90, 0.3)',
      color: "white",
      display: 'flex',
      backgroundSize: '35vh 75vh',
    },
  }))

const ChatRoom = (location) => {
    const {user, to, userIcon, toIcon} = location.state || {user: "aaa", to: "bbb"};
    const [msgs, setMsgs] = React.useState([
        {
          id: 1,
          text: 'Hello developer',
          createdAt: new Date(),
          user: {
            id: 2,
            name: 'React',
            avatar: 'https://facebook.github.io/react/img/logo_og.png',
          },
        },
      ],);

    const addMessage = (msg) => {
        const newMsg = msgs.push([]);
        setMsgs(newMsg);
    }

    const onSend = (msg) => {
        console.log(msg);

        // if (typeof window !== 'undefined') {
        //     fetch('http://localhost:3000/msg/get', {
        //         method: 'PUT',
        //         headers: {
        //             'Accept': 'application/json',
        //             'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify({
        //             "account": user,
        //             "to": to,
        //             "msg": msg,
        //         })
        //         })
        //         .then(res => res.json())
        //         .then(data => {
        //             if (data.success) {
        //                 console.log('successfully added to db')
        //             } else {
        //                 console.log('ERROR')
        //             }
        //         })
        // }
    }
    
    useEffect(() => {
        const socket = socketIOClient(ENDPOINT);
        socket.emit('join', user);
        socket.on('message', msg => addMessage(msg));
        if (typeof window !== 'undefined') {
            fetch('http://localhost:3000/msg/get', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    "account": user,
                    "to": to,
                })
                })
                .then(res => res.json())
                .then(data => {
                    if (data.success) {
                        const fetchedMsgs = data.data.map((row, i) => {
                            return {
                                id: i,
                                text: row.msg,
                                createdAt: mysqlTimeStampToDate(row.time),
                                user: {
                                    id: row.userName,
                                    name: row.userName,
                                    avatar: row.userName === user ? userIcon : toIcon,
                                }    
                            }
                        })
                        console.log(fetchedMsgs);
                    } else {
                      console.log('ERROR')
                    }})
        }
        return () => socket.disconnect();
    }, [])

    return (
        <div>
            <ChatRoomHeader userName={user} />
            <GiftedChat
                messages={msgs}
                onSend={(messages) => this.onSend(messages)}
                user={{
                  id: 1,
                }}
            />
        </div>)


}

export default ChatRoom;
