import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Menu, MenuItem } from '@material-ui/core';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { useState } from 'react';
import { navigate } from 'gatsby';


function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  formControl: {
    minWidth: 150,
    margin: theme.spacing(1)
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));




export default function SignUp() {
  const classes = useStyles();
  const [username, setUN] = useState();
  const [password, setPD] = useState();
  const [name, setName] = useState();
  const [age, setAge] = useState();
  const [matching, setMatching] = useState(1);
  const [img, setImg] = useState();
  const [f, setFn] = useState();
  const [phoneNum, setPhone] = useState();

  function signUp(e) {
    e.preventDefault()
    if (typeof window !== 'undefined') {
      const formData = new FormData();
      const axios = require("axios");
      const config = {
        headers: {
            'content-type': 'multipart/form-data'
        }
    };
      formData.append('file', img);
      axios.post('https://matchsitebackend.herokuapp.com/upload/img', formData, config).then((res) => {
        setFn(res.data.name);

        fetch('https://matchsitebackend.herokuapp.com/LoginPage/reg', {
          method: 'PUT',
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({
              "username": username,
              "password": password,
              "name": name,
              "age": age,
              "matchingLoc": matching,
              "img": res.data.name,
              "phone": phoneNum,
          })
          })
          .then(res => res.json())
          .then(data => {
              if (data.success) {
                console.log('registered!')
                navigate('/login');
              } else {
                alert('user prob already exists')
                navigate('/signUp');
              }
          })
      }).catch(err => {
        alert('file upload failed!');
      });

    }
  };


  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <form className={classes.form} noValidate onSubmit={signUp}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                autoComplete="fname"
                name="username"
                variant="outlined"
                required
                fullWidth
                id="username"
                label="user name"
                autoFocus
                color='secondary'
                onChange={(e) => setUN(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="password"
                label="password"
                color='secondary'
                onChange={(e) => setPD(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="name"
                label="name"
                color='secondary'
                onChange={(e) => setName(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="age"
                label="age"
                color='secondary'
                onChange={(e) => setAge(Number(e.target.value))}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="phone"
                label="phone number"
                color='secondary'
                onChange={(e) => setPhone(e.target.value)}
              />
            </Grid>
            <Grid item xs={5} sm={5}>
                <div>
                  <Typography>Profile Image:</Typography>
                  <input type="file" onChange={(e) => setImg(e.target.files[0])}/>
                </div>
              </Grid>
            </Grid>
            <Grid item xs={12} sm={12}>
                <FormControl className={classes.formControl} color='secondary'>
                    <TextField
                        label="matching Location"
                        select
                        id="matching Loc"
                        value={matching}
                        onChange={(e) => setMatching(Number(e.target.value))}
                    >

                    <MenuItem value={1}>Taipei City</MenuItem>
                    <MenuItem value={2}>Others</MenuItem>
                    </TextField>
                </FormControl>
            </Grid>

            
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign Up
          </Button>
          <Grid container justify="flex-end">
            <Grid item>
              <Link href="/login" variant="body2">
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Container>
  );
}
